ruleBookContent:
$;function getImgList(){
	var len = +html.match(/第 \d+\/(\d+)頁/)[1];
	var pic = html.match(/cpimg\" src="([^]*?)\d+(\.\w+)"/);
    var server = pic[1];
    var ext = pic[2];
    var piclist = [];
	for(var i=0;i<len;++i){
		piclist[i] = server+(i+1)+ext ;
	}
	return piclist;
}
