function getImgList(){     
var p = /pcount=(\d+)/;     
var p1 = /(https:\/\/mh\S+\/upload\/\S+)\/(\d+).jpg/;
var mp = html.match(p1)[1];
var jpg = html.match(p)[1];
var jpf = html.match(p1)[2];
var jpl= jpf.length;
var piclist = new Array(jpg); 
if ( jpl>1) {
for (var i = 0 ; i < jpg ; i++) {
piclist[i] = mp + "/"+(new Array(jpl).join('0') +(i+Number(jpf))).slice(-jpl)+".jpg";}
}
else{
for (var i = 0 ; i < jpg ; i++) {
piclist[i] = mp + "/"+(i+Number(jpf))+".jpg";} 
}
return piclist; 
}

function getImgList(){
    var p = "chapterImages = \\[(.*?)\\]";
    var p1 = "chapterPath = \"(.*?)\"";
    var chapterPath  = html.match(p1)[1];
    var path = html.match(p)[1].replace(/\"/g, "").split(",");
    var piclist = new Array([path.length]);
    for (var i = 0; i < path.length; i++) {
        piclist[i] = "http://res.gufengmh.com/"+chapterPath + path[i];
    }
    return piclist;
}

