tag.a.1@href@js:
result=java.ajax('http://m.665txt.com'+result);

//尝试获取必要书籍信息
var intro=result.match(/property="og:description" content="(.*)/);
var cover=result.match(/property="og:image" content="([^"]*)/);
var last=result.match(/property="og:novel:latest_chapter_name" content="([^"]*)/);
var tag1=result.match(/分类：.+?>([^<]*)/);
var tag2=result.match(/状态：([^<]*)/);
var url=result.match(/"(\/read\/\d+\/\d+\/)"/);
function check(a){return (a)? a[1]:'';}

java.put('intro',check(intro));
java.put('cover',check(cover));
java.put('last',check(last));
java.put('url',check(url));

result=check(tag1)+','+check(tag2).replace(/小说/,'').replace(/连载中/,'连载').replace(/已完结/,'完结');

