function getImgList() {     
var str = html.match(/onclick=\"chgImg\((\d+)\).*src=\"(.*?\d{4,}\/)1(\.\w+)\"/);
var pn = parseInt(str[1]);          
var piclist = new Array([pn]);        
for (var i = 0; i < pn; i++) {   
piclist[i] = str[2]+(i+1).toString()+str[3];        
}       
return piclist;    
}

