var mp = html.match(p1)[1];
var jpg = html.match(p)[1];
var jpf = html.match(p1)[2];
var jpl= jpf.length;
var piclist = new Array(jpg); 
if ( jpl>1) {
for (var i = 0 ; i < jpg ; i++) {
piclist[i] = mp + "/"+(new Array(jpl).join('0') +(i+Number(jpf))).slice(-jpl)+".jpg";}
}
else{
for (var i = 0 ; i < jpg ; i++) {
piclist[i] = mp + "/"+(i+Number(jpf))+".jpg";} 
}
return piclist; 
}

function getImgList(){  
    var p = /z_img='\[(.*)\]'/
    var strs = html.match(p)[1].split(',');
    if(strs != null){
        var piclist = new Array([strs.length]);
        for (var i = 0; i < strs.length; i++) {
            var a = /(\\\/([-A-Za-z0-9+&@##/%?=~_|!:,.;]+))/g
            var c = strs[i].match(/\"(.*)\"/)[1].match(a)
            var myurl = "https:/"
            c.forEach(function(value, index, array) {
                if(value.indexOf('img001.yayxcc.com') > -1) {
                    value = value.replace('img001.yayxcc.com', 'res.comic123.net');
                }
                myurl += value.slice(1)
            });
            piclist[i] = myurl;
        }
        return piclist;
    } else {
        return null;
    }
};

function getImgList(){
    var p = "chapterImages = \\[(.*?)\\]";
