ruleSearch.bookList:
id.hotcontent!0@.item-cover
@js:result.toArray().slice(0,8).map(o=>{
    var detail = String(o.select('a').attr('href')).replace(/^\//, baseUrl.match(/(.+?\/\/[^\/]+\/)/)[1]);
    var J = org.jsoup.Jsoup.parse(java.ajax(detail));
    var oText = s=>String(o.select(s).text().trim()).replace(/.+：/, '');
    var jText = s=>String(J.select(s).text().trim()).replace(/.+：/, '');
    return {
       name:  oText('h3'),
       author:J.select('meta[property="og:novel:author"]').attr('content'),
       tags:   J.select('meta[property="og:novel:category"]').attr('content'),
       latest: String(J.select('meta[property="og:novel:latest_chapter_name"]').attr('content')).replace(/[\(（【].*[求更谢乐发推].*[】）\)]/,'').replace(/.*(第[^章]+章)/,'$1')+ String(J.select('meta[property="og:novel:update_time"]').attr('content')).replace(/(\d+-\d+-\d+).*/,'（$1）'),
       intro: String(J.select('meta[property="og:description"]').attr('content')).replace('内容简介：','').trim(),
       cover: J.select('meta[property="og:image"]').attr('content'),
       catalogUrl:detail
    }
}).filter(v =>v.name);

