ruleToc.chapterList:
@js:
var list=[];
var datatxt=result.match(/booklist clearfix([^@]*)<a href="https:\/\/book\.189dg\.com/)[1];
var strongList=datatxt.split("</strong>");
var data="";
for(var i=2,len=strongList.length;i<len;i++){
	data=data.concat(strongList[i]);
}
var datalist=data.split("<a href=");
if(datalist.length>1){
	for(var i=1,len=datalist.length;i<len;i++){
		var temp=datalist[i].match(/(.*\.html)>(.*)<\/a></);
		list.push({
			"chapterName":temp[2],
			"chapterUrl":baseUrl+temp[1]
		});
	}
}
list;

ruleSearch.bookList:
@js:
var list=[];
var bookSound=baseUrl.match(/(.*?\/\/.*?)\//)[1];//? 非贪婪
if(result.indexOf("<script>alert")!=-1){
	//搜索出现问题,总会有问题的.软件默认一次搜索进行多次.网站规定30秒搜索1次
}else{
	var data=result.match(/container flex flex-wrap mb20([^@]*)container"/)[1];
	var dataList=data.split("category-div");
	for(var i=1,len=dataList.length;i<len;i++){
		var temp=dataList[i].match(/flex flex-between commend-title"><a href="(.*)"><h3>([^@]*)<\/h3>.*<span>([^@]*)<\/span>[^@]*"intro indent">([^@]*?)<\/div>/);
		list.push({
			"name":temp[2],
			"author":temp[3],
			"intro":temp[4],
			"bookUrl":bookSound+temp[1]
		});
	}
}
list;

