var chapters = [];
var regex = /<a\s*href="(\/\d+\/read_\d.html)"[^"]+"([^"]*)/g;
var match;
while ((match = regex.exec(result)) != null) {
	chapters.push({ k: match[1], v: match[2] });
}
var getCount = result.match(/latest_chapter_url[^_]+_(\d+)/)[1] - chapters[chapters.length - 1].k.match(/read_(\d)/)[1];
if (getCount > 0) {
	var link = 'https://www.xiashutxt.com/api/ajax/zj?id=' + baseUrl.match(/\d+/)[0] + '&num=' + getCount + '&order=asc';
	var req = java.ajax(link);
	regex = /href="([^"]*)[^=]*="[^"]*/g;
	while ((match = regex.exec(req)) != null) {
		chapters.push({ k: match[1], v: match[2] });
	}
}
chapters
</js>

