ruleSearch.bookList:
.search-list-note + .list-book-col1 li
@js:
result.toArray().slice(0).map(o =>{
    var oText = selector =>String(o.select(selector).text().trim()).replace(/.+：/, '');
    return {
        name: oText('.book-title'),
        author: o.select('.book-info-sub').eq(0).text(),
        tags: oText('.book-info-sub:gt(0)').replace(/\s+/g,',').replace(/已|中/, ''),
        latest: oText('.book-chapter').replace(/[\(（].*[求更谢乐发推].*[）\)]/,''),
        intro: oText('.book-summary'),
        cover: o.select('.book-cover img').attr('src') || 'http://suo.im/6mlA9F',
        catalogUrl: o.select('.book-title').attr('href')
    }
}).filter(v =>v.name);

