var list=[];
var datatxt=result.match(/booklist clearfix([^@]*)<a href="https:\/\/book\.189dg\.com/)[1];
var strongList=datatxt.split("</strong>");
var data="";
for(var i=2,len=strongList.length;i<len;i++){
	data=data.concat(strongList[i]);
}
var datalist=data.split("<a href=");
if(datalist.length>1){
	for(var i=1,len=datalist.length;i<len;i++){
		var temp=datalist[i].match(/(.*\.html)>(.*)<\/a></);
		list.push({
			"chapterName":temp[2],
			"chapterUrl":baseUrl+temp[1]
		});
	}
}
list;

