@js:
var list = new Array();
if (result.indexOf("title_list[0]")!=-1) {
  var str = result.match(/chapter\_list\[\d+\].*?\((.*?)\)/g).join().replace(/href=\"/g,"href=\"https://m.pipimov.com");
  var href = str.match(/https.*?html/g);
  var text = str.match(/title=".*?"/g).join().replace(/title=/g,"").replace(/\"/g,"").split(/,/);
  var a = href.length.toString();
  for (var i=0; i<a; i++) {
    list.push({href:href[a-1-i], text:text[a-1-i]});
  }
}
list;

