function getImgList() {     
var str = html.match(/files\":\[(.*?)\]/);
var list = str[1].match(/comic[^\"]+/g);
var piclist = new Array([list.length]);        
for (var i = 0; i < list.length; i++) {   
piclist[i] = "http://images.shuamanhua.com/" + list[i];        
}       
return piclist;    
}

function getImgList(){
    var str = html.match(/murl_e=\"(.*?)\"/)[1];
    var list = window.atob(str).split("\$qingtiandy\$");
    return list;
}

