function getImgList(){  
    var p = /z_img='\[(.*)\]'/
    var strs = html.match(p)[1].split(',');
    if(strs != null){
        var piclist = new Array([strs.length]);
        for (var i = 0; i < strs.length; i++) {
            var a = /(\\\/([-A-Za-z0-9+&@##/%?=~_|!:,.;]+))/g
            var c = strs[i].match(/\"(.*)\"/)[1].match(a)
            var myurl = "https:/"
            c.forEach(function(value, index, array) {
                if(value.indexOf('img001.yayxcc.com') > -1) {
                    value = value.replace('img001.yayxcc.com', 'res.comic123.net');
                }
                myurl += value.slice(1)
            });
            piclist[i] = myurl;
        }
        return piclist;
    } else {
        return null;
    }
};

