ruleSearch.bookList:
tr!0
<js>
result.toArray().slice(0,3).map(o=>{
    var detail = o.select('td:eq(0) a').attr('href');
    var J = org.jsoup.Jsoup.parse(java.ajax(detail));
    var oText = s => String(o.select(s).text()).trim();
    var jText = s => String(J.select(s).text()).trim();
    return {
       name:  oText('td:eq(0)').replace(/\[.*\]/g,''),
       author: oText('td:eq(2)'),
       tags:  oText('td:eq(5)'),
       num: oText('td:eq(3)') + '字',
       latest: oText('td:eq(1)').replace(/(正文)?(\s+|_)/,'').replace(/[\(（【].*[求更谢乐发推].*[】）\)]/,'')+ '(' + oText('td:eq(4)')+')',
       intro: jText('#intro'),
       cover: String(detail).replace(/(\d+)_(\d+).*/,'files/article/image/$1/$2/$2s.jpg'),
       catalogUrl: detail
    }
});
</js>

@js:
JSON.parse(result).BookList.slice(0, 5).map(o =>{
    var option = JSON.stringify({"method":"POST","body":"book_id=" + o.bookid});
    var detail = 'http://www.asike.top/api/detail-book,'+option;
    var J = JSON.parse(java.ajax(detail));
    return {
        name: o.bookName,
        author: o.bookAuthor,
        tags:  o.category + ','+J.book.bookScore + '分 / '+ J.book.bookStatus.replace(/已|中/, ''),
        num: J.book.bookSize + '万字',
        latest:  (J.book.totalChapter > 100) ? ('第' + J.book.totalChapter + '章') + '（' + java.timeFormat(J.book.updateTime) + '）': J.contentList.pop().chapterName + '（' + java.timeFormat(J.book.updateTime) + '）',
        intro: o.introduction,
        cover: o.bookCover,
        catalogUrl:detail
    }
});

ruleToc.chapterList:
@js:
var data=[];
var json = JSON.parse(result).book;
var id = json.bookId;
var page = parseInt(json.totalChapter / 100);
var url = 'http://www.asike.top/api/content-list/' + id + '/';
for (var i = 0; i <= page; i++) {
    var j = JSON.parse(java.ajax(url + i)).contentList;
    data = data.concat(j);
}
data.map(o=>({
    title:o.chapterName,
    url:'http://content.asike.top'+o.contentUrl
}));

+@js:
var reg=new RegExp(/<dd(.).+?"(\d+\.html)">(.*?)<\/a>/g);
var list1=[];
var list2=[];
var tmp,url,name;
var i=-1;
while((tmp=reg.exec(result))){
url=tmp[2];
name=tmp[3];
if(tmp[1].match(/>/)){
i=i+1;
list1.push({name:name,url:url});
}
else
list2.push({name:name,url:url});

if(i==1){
i=-1;
list1=list1.concat(list2.reverse());
list2=[];
}
}
list1;

