ruleToc.chapterList:
@js:
var data=[];
var json = JSON.parse(result).book;
var id = json.bookId;
var page = parseInt(json.totalChapter / 100);
var url = 'http://www.asike.top/api/content-list/' + id + '/';
for (var i = 0; i <= page; i++) {
    var j = JSON.parse(java.ajax(url + i)).contentList;
    data = data.concat(j);
}
data.map(o=>({
    title:o.chapterName,
    url:'http://content.asike.top'+o.contentUrl
}));

