ruleSearch.bookList:
.list-group-item!0
<js>
result.toArray().slice(0,5).map(o=>{
    var detail = String(o.select('.col-xs-3 a').attr('href')).replace(/^\//, 'http://www.qiuxiaoshuo.org/');
    var J = org.jsoup.Jsoup.parse(java.ajax(detail));
    return {
       name:  o.select('div.col-xs-3 a').text(),
       author:o.select('div.col-xs-2').first().text(),
       tags:  o.select('div.col-xs-1 i').text(),
       latest: String(J.select('meta[property=og:novel:latest_chapter_name]').attr('content')).replace(/(Ê≠£Êñá)?(\s+|_)/,'').replace(/[\(Ôºà„Äê].*[Ê±ÇÊõ¥Ë∞¢‰πêÂèëÊé®].*[„ÄëÔºâ\)]/,''),
       intro: String(J.select('meta[property=og:description]').attr('content')).replace(/^\s*/,'<br>').trim(),
       cover: J.select('meta[property=og:image]').attr('content'),
       catalogUrl:J.select('meta[property=og:novel:read_url]').attr('content')
    }
});
</js>

