ruleSearch.bookList:
class.novelslist2@ul@li!0
<js>
result.toArray().slice(0,8).map(o=>{
    var detail = String(o.select('.s2 a').attr('href')).replace(/^\//, baseUrl.match(/(.+?\/\/[^\/]+\/)/)[1]);
    var J = org.jsoup.Jsoup.parse(java.ajax(detail));
    return {
       name:  o.select('.s2 a').text(),
       author:o.select('.s4').text(),
       tags:  J.select('meta[property="og:novel:category"]').attr('content'),
       latest: String(o.select('.s3 a').text()).replace(/[\(ï¼ˆ].*[æ±‚æ›´è°¢ä¹å‘æ¨].*[ï¼‰\)]/,'')+ String(o.select('.s6').text()).replace(/(\d+-\d+).*/,'ï¼ˆ$1ï¼‰'),
       intro:  J.select('meta[property="og:description"]').attr('content'),
       cover: J.select('img').attr('src'),
       catalogUrl: detail.replace('www','m')
    }
}).filter(v=>v.name);
</js>

