ruleSearch.bookList:
#sitebox dl<js>
result
  .toArray()
  .slice(0, 3)
  .map(function (el) {
    var url = String(el.select('a').eq(1).attr('href')).replace(
      /^\/([^\/])/,
      baseUrl.match(/(.+\/\/.+?\/)/)[1] + '$1'
    );
    var J = org.jsoup.Jsoup.parse(String(java.ajax(url)));
    var og = (selector) =>
      String(J.select('[property="og:' + selector + '"]').attr('content'));

    return {
      name: og('novel:book_name'),
      author: og('novel:author'),
      kind: og('novel:category') + ',' + og('novel:status').replace(/中|已/,'').replace(/中|已/,''),
      latest: og('novel:lastest_chapter_name') + ' • ' + String(og('novel:update_time')).replace(/(\d+:.+)/,''),
      cover: og('image'),
      wordCount: J.select('#count span').eq(1).text(),
      intro: og('description'),
      url: url,
    };
  });
</js>
