id.htmlContent@html<js>
var n=String(baseUrl).match(/.*page=(\d+)/)[1];
if (n>1){
var header="@Header:\{referer:\""+String(baseUrl)+"\"\}";
var url=header+String(baseUrl).replace(/http:\/\/www.xs63.com\/.*?\/(.*?)\/(\d+).html\?page=(\d+)/g,"http://www.xs63.com/index/bookinfo/?cid=$2&bid=$1&page=$3&time="+{{Date.now()}}+"@cid=$2&bid=$1&page=$3&time="+{{Date.now()}});
var t=String(JSON.parse(java.ajax(url)).data);
result+=String(t);
}
result=String(result).replace(/<p\s*class="ins">[\s\S]*?<\/p>/g,"");
result;
</js>

