ruleToc.nextTocUrl:
@js:
var url=baseUrl.replace(/(.+\/\d+\/)\d+.html/,'$1');
var i=2;
var list=[];
for(i;i<(@get:{num}/100)+1; i++){
baseUrl=url+i+'.html';
list.push(baseUrl);
}
list;

ruleSearch.bookList:
+<js>
String(result).match(/<tr id="nr">[\w\W]+?tr>/gm).map(x=>{
let p = x.split('\n');
return {
	a:p[1].match(/<a.+?>(.+?)</)[1],
	b:p[3].match(/>(.+?)</)[1],
	c:p[6].match(/>(.+?)</)[1],
	d:p[2].match(/blank">(.+?)</)[1],
	e:p[2].replace(/.+?(\d+)\/(\d+)[\w\W]+/,'/files/article/image/$1/$2/$2s.jpg'),
	f:p[1].match(/href="(.+?)"/)[1]
	}
});
</js>

