ruleSearch.bookList:
#alistbox
<js>
result.toArray().slice(0, 3).map(el => {
	var url = String(el.select('a:eq(0)').attr('href')).replace(/^\//, baseUrl.match(/(.+?\/\/[^\/]+\/)/)[1]);
	var J = org.jsoup.Jsoup.parse(java.ajax(url));
	var o = selector => String(J.select(selector).text());
	var og = selector => String(J.select('[property="og:' + selector + '"]').attr('content'));
	return {
		name: og('novel:book_name').replace(/(全文|小说|免费阅读|最新章节).*|[(（].*[）)]/g, ''),
		author: og('novel:author'),
		kind: og('novel:category') + ',' + o('.top').match(/状态：(.*)\s更/)[1].replace(/中|已/, ''),
    wordCount: o('.top').match(/字数：(.*)字/)[1],
		latest: og('novel:latest_chapter_name').replace(/^(正文|VIP章节|最新章节)?(\s+|_)|[\(（【].*[求更谢乐发推].*/g, '') + ' • ' + o('.top').match(/时间：(.*)\s/)[1].replace(/\//g, '-'),
		intro: o('.intro').replace(/.*(观看小说|简介)[:：]|(各位书友|Tags).*/g, '').replace(/\s+/g, ''),
		cover: og('image'),
		url: url,
	};
}); 
</js>

.sear li
<js>
result.toArray().slice(0,3).map(o=>{
    var url = baseUrl.match(/(.+\/\/.+?\/)/)[1] + o.select('a').attr('href');
    var J = org.jsoup.Jsoup.parse(java.ajax(url));
    var o = selector =>String(J.select(selector).text());
    return {
        name: o('h1').replace(/\s.*/,''),
        author: o('.cl1 ul').match(/作者：(.*)\s大小/)[1],
        kind: o('.cl1 ul').match(/类别：(.*)\s作者/)[1],
        wordCount: o('.cl1 ul').match(/大小：(.*)\s状态/)[1],
        latest: o('.cl1 ul').match(/(入库.*)\s在线/)[1],
        intro: o('.jj'),
        cover: J.select('.cl1 img').attr('src'),
        url: url,
    }
});
</js>

ruleToc.chapterList:
@js:list = [];
if (baseUrl.match(/id/)) {
    p = result.match(/=(\d+)">最后1页/)[1];
    for (i = 0; i <= p; i++) {
        list.push({
            n: '第' + [i + 1] + '页',
            u: baseUrl.replace(/,.*$/, '&yeshu=' + i + ',{charset:"gbk"}')
        })
    }
} else list.push({
    n: '空',
    u: ''
});
list

