ruleSearch.bookList:
.grid tr!0
<js>
result.toArray().slice(0,5).map(o=>{
    var detail = o.select('td:eq(0) a').attr('href');
    var J = org.jsoup.Jsoup.parse(java.ajax(detail));
    var oText = selector =>String(o.select(selector).text().trim()).replace(/.+：/, '');
    return {
       name:  oText('td:eq(0)'),
       author: oText('td:eq(2)'),
       tags:   oText('td:eq(5)'),
       num:   oText('td:eq(3)') + '字',
       latest:  oText('td:eq(1)') +  oText('td:eq(4)').replace(/(\d+-\d+-\d+).*/,'（$1）'),
       intro:  J.select('#intro').text(),
       cover: J.select('#fmimg img').attr('src'),
       catalogUrl: detail
    }
}).filter(v =>v.name);
</js>

