ruleChapterList:
.chapter a<js>
var bid = baseUrl.match(/\/(\d+)/)[1];
var data = java.ajax('https://xiaoshuo.sogou.com/api/pc/v1/book/read/state@bid='+bid);
var cids = JSON.parse(data).data.chaptersRS.map(v => v.cid).join('|');
result
  .toArray()
  .map(a => {
    var cid = String(a.attr('pbtag'));
    return {
      text: (cids.includes(cid) ? '' : '🔒') + a.text(),
      href: a.attr('href')
    }
  })
</js>
