$function getImgList() {
  var str = html.match(/SMH.reader\(([\S\s]+)\).init\(\);/)[1];
  eval("var cInfo = "+str);
  var path = cInfo.images[0].match(/(\/comic.*?)\d{4}(\.\w{3})/)
  var piclist = new Array(cInfo.images.length);
  for (var i = 0; i < cInfo.images.length; i++) {
    piclist[i] = cInfo.host+path[1]+(new Array(4).join("0") +i).slice(-4)+path[2];
  } 
  return piclist;
}

