ruleChapterUrlNext:
text.尾页.0@href
<js>
t=/\[(.+_)([2-9]\b|\d{2,3})/;
m=String(result).match(t);
if(m==null){result=null;}
else{n=m[2];n=parseInt(n);page=[];
for(i=2;i<n+1;i++){page[i-2]=m[1]+String(i)+"/"};
result=page;}
</js>

