    eval(html.match(/var chapterImages.*chapterPath = "([^"]*?)";/)[0]);
if(chapterImages[0].substr(0,4)=='http') {
return chapterImages;
}
	return chapterImages.map(function(image){
return "http://img.xmhjtx.com/" +chapterPath+ image;});
