$function getImgList() {
  var str = html.match(/var chapterImages = \[(.*?)\];var chapterPath = "(.*?)";/);
  var list = str[1].replace(/"/g,"").split(/,/);
  var path = str[2];
  var host = "https://mhpic.dongzaojiage.com/";
  var piclist = new Array(list.length);
  for (var i = 0; i < list.length; i++) {
    if (list[i].search(/^https?:\/\/(images.dmzj.com|imgsmall.dmzj.com)/i)==0) {
      piclist[i] = host + "showImage.php?url=" + list[i];
    }
    else if (list[i].search(/^[a-z]\//i)==0) {
      piclist[i] = host + "showImage.php?url=https://images.dmzj.com/" + list[i];
    }
    else if (list[i].search(/^(http:|https:|ftp:|^)\/\//i)==0) {
      piclist[i] = list[i];
    }
    else {
      piclist[i] = host + path + list[i];
    }
  }
  return piclist;
}

