var id = a[1];
var bid = a[2];
var url = "https://www.du00.com/t/t.php?id=/"+id+"&did="+bid+"&qid=";

doc.select("#list a").toArray().map(a=>({
   text:a.text(), 
   link:(function(a,url){
              var curl=a.attr("href");
              var cid=curl.match(/(\d+)\.html/)[1];
              return url+cid+"&page="+cid;
})(a,url)
}))

