fgwb = qbgl.split('作者公告');

  if (fgwb.length > 1) {
   zpjj = fgwb[0];
   zzgg = fgwb[1];
    //zpjj='\n作品简介\n'+zpjj;
    zzgg='\n公告\n'+zzgg;
  }else{
  zpjj = qbgl;
    //zpjj='\n作品简介\n'+zpjj;
