ruleChapterList:
+<js>
//二级目录，Json
var jdoc = JSON.parse(result).Data;
var chUrl = 'https://m.8kana.com/chapter/content/';
var volUrl = 'https://translate.google.cn/#view=home&op=translate&sl=zh-CN&tl=en&text=';
var list = [];

function format(title){
  return title.trim().replace(/^(\d+)(?![\s\d卷部章节回.])/, '$1 ').replace(/\s+/g, "\x20\x20");
}

jdoc.Volumes.forEach((vol,idx) => {
  var chs = jdoc.Chapters.filter(c => c.VolumeId == vol.VolumeId).map(ch => ({
    text: (ch.isVip === '1' ? '💰' : '').padStart(4, "\u2000") + format(ch.Title),
    href: chUrl + ch.ChapterId,
    name: format(ch.Title)
  }));

  var volInfo = '目录(' + chs.length + ")：\n";
  volInfo += chs.slice(0,10).map(v => v.name).join("\n");
  volInfo += chs.length > 10 ? "\n……" : '';
  list.push({
    text: '🔻[ ' + format(vol.Title) + ' ]',
    href: volUrl + encodeURIComponent(volInfo)
  });
  list = list.concat(chs);
});

result = list;
</js>

