ruleSearchList:
.list_center h3 a@js:
importClass(org.jsoup.Jsoup);
var data = [];
result.toArray().slice(0, 3).forEach(function (a) {
  var url = a.attr('href');
  var J = Jsoup.parse(java.ajax(url));
  data.push({
    name: J.select('h1').text(),
    author: J.select('.p_author a').text(),
    tags: J.select('#count span').toArray().slice(0, 3).map(function (el) {
      return String(el.text()).replace(/\s+|中|已/g, '').replace(/(\d+万)/, '$1字').trim();
    }),
    latest: J.select('.chaw a').eq(0).text(),
    intro: J.select('#bookintro p').eq(0).text(),
    cover: J.select('#bookimg img').attr('src'),
    url: url
  });
});
data;
