var n1 = new RegExp(/<h2>(.{0,66})<\/h2>\s*<ul class="catalogue-list less/g);
var n3 = new RegExp(/href="\S{6,99}">\s+(\S+.+)<\/a/g);
var n,n_pre = '',n_sub,n_temp,t = i= -1,p = [],p_tmp,i_tmp,ppp=[],q,w;



//只保留包含卷和章列表的内容
var lsqaa = new RegExp('div class="cnt-box catalogue"(\\n|.)+div class="cnt-box book-tags"');
var lswaa = lsqaa.exec(result)[0]


// 获取网址列表
while ((n_temp = url1.exec(lswaa)) != null) {
ppp.push({q: n_temp[1]});
}

//获取每卷的标题
while ((n_temp = n1.exec(lswaa)) != null) {
 i = i + 1;
	p.push({
		q: n_temp[1],
		w: n1.lastIndex
	});
}
i_tmp = i;n_tmp='';

while ((n_temp = n3.exec(lswaa)) != null) {
	//获取每话的标题
	n_sub = n_temp[1];
	//判断VIP
	if ((n_sub.match(/icon-lock/)) != null) {
		n_sub = n_sub.match(/<i class='icon icon-lock'><\/i>(.+)/)[1];
		n_sub = '🔒' + n_sub;
	}




	//添加卷标题和二级标题
	if (p[0] != null) {
		i = i_tmp;
		p_tmp = p[i].w;
		while (p_tmp > n3.lastIndex) {
			i = i - 1;
			p_tmp = p[i].w;
		}
		n_pre = p[i].q;
if(n_pre!=n_tmp){
   list.push({
		  k: '♦'+n_pre+'♦',
		  v: 'http://localhost/'+n_pre
	});n_tmp=n_pre;
}
		n_pre ='';
	}
	//保存数据到数组


	n = n_pre + n_sub;
	t = t + 1;
	list.push({
		k: n,
		v: ppp[t].q
	});n_pre = '';
}
list;

